import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
// import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

class List extends Component {
  async checkTask(task) {
    await fetch(`http://localhost:3001/updateTask/${task._id}`, 
    {
      method: 'PUT',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
       ...task, taskStatus: 'Pronto'
      })
    }
  );

  this.props.loadTasks();
  }

  async checkTaskOnGoing(task) {
    await fetch(`http://localhost:3001/updateTask/${task._id}`, 
    {
      method: 'PUT',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
       ...task, taskStatus: 'Em andamento'
      })
    }
  );

  this.props.loadTasks();
  }

  async deleteTask(task) {
    if(window.confirm(`Are you sure you want to delete: ${task.task}? `)) {
      await fetch(`http://localhost:3001/deleteTasks/${task._id}`, {method: 'DELETE'});
      // console.log(`to aqui hein, no id: ${task._id}, task: ${task.task}`);
      this.props.loadTasks();
    }
  }

  render() {
    const { tasks } = this.props
    return(
      <div>
        <Card>
          <Card.Body>
            <Table responsive>
              <tbody>
                {Object.values(tasks).map((task, index) => {
                  return <tr key={task.id}>
                    <td className="col-md-10" >{task.task} - {task.taskStatus}</td>
                    <td className="col-md-5" >{task.details}</td>
                    <td>
                      {
                        task.taskStatus === "Pendente"
                        ? <a className="check" href="#">
                          <FontAwesomeIcon icon="calendar-day"
                          onClick={() => this.checkTaskOnGoing(task)} size='lg'/>
                        </a>
                        : <a className="check" href="#">
                        <FontAwesomeIcon icon="check-circle"
                        onClick={() => this.checkTask(task)} size='lg'/>
                      </a>
                      }
                    </td>
                    <td>
                        <a className="delete" href="#" onClick={() => this.deleteTask(task)}>
                        <FontAwesomeIcon icon="trash-alt"/>           
                        </a>
                        <a className="check" href="#" onClick={() => this.deleteTask(task)}>
                        <FontAwesomeIcon icon="pencil-alt"/>  
                        <Button onClick={e => setShow(true)} variant="dark" className="float-right create_task_btn"><FontAwesomeIcon icon="calendar-plus" size="lg" /></Button>

<Modal show={show || false} onHide={e => setShow(false)}>
  <Modal.Header closeButton>
    <Modal.Title>Create New Task</Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <Form.Control className="form-group" type="email" placeholder="What is yout task?" value={task || ''} onChange={e => setTask(e.target.value)} />
    <Form.Control className="form-group" type="email" placeholder="Tell me details about it" value={details || ''} onChange={e => setDetails(e.target.value)} />
    <Form.Select className="form-group" class="form-select" multiple aria-label="multiple select example" onChange={e => setTaskStatus(e.target.value)}>
        <option selected>Select the status of your task</option>
        <option value="Pendente">To Do</option>
        <option value="Em andamento">On Going</option>
        <option value="Pronto">Done</option>
    </Form.Select>
  </Modal.Body>
  <Modal.Footer>
    <Button variant="secondary" onClick={e => setShow(false)}>
      Close
    </Button>
    <form onSubmit={handleSubmit}>
      <Button variant="dark" type="submit">
        Create
      </Button>
    </form>
  </Modal.Footer>
</Modal>         
